import React from 'react';
import "./home.scss"
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import AccessTimeIcon from '@material-ui/icons/AccessTime';
import MailOutlineIcon from '@material-ui/icons/MailOutline';
import PhoneIcon from '@material-ui/icons/Phone';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const useStyles = makeStyles(theme => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(8, 0, 6),
    marginTop: 70
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(6),
  },
}));

const cards = [1, 2, 3, 4, 5, 6, 7, 8, 9];
function Home() {
    const classes = useStyles();
    return (
        <div className="home-container">

        <React.Fragment>
        <CssBaseline />
        <AppBar position="absolute" color="default" className="header">
        <Toolbar>
          <Typography variant="h6" color="inherit" noWrap>
            Welcome to our Agency           
          </Typography>
        </Toolbar>
      </AppBar>
        <main>
          {/* Hero unit */}
          <div className={classes.heroContent}>
            <Container className="info-section">
            <Grid container spacing={4}>
            <Grid  container item sm={4} >
              <Typography component="h3" variant="h4" align="center" color="textPrimary" gutterBottom>
                LOGO
              </Typography>
            </Grid>  
              <Grid container item justify="flex-end"  sm={8} direction="row">
                  <Grid container sm={4} spacing={4} direction="row"   alignItems="center" item>
                    <Grid item>
                        <PhoneIcon/>
                    </Grid>
                    <Grid item>
                        <Grid container alignItems="flex-start"  item>
                        Phone
                        </Grid>
                        <Grid item>
                        1-888-123-45678
                        </Grid>
                    </Grid>
                  </Grid>
                  <Grid container sm={4} spacing={4} direction="row"  alignItems="center"  item>
                        <Grid  item>
                            <MailOutlineIcon/>
                        </Grid>
                        <Grid item>
                            <Grid container alignItems="flex-start" item>
                            E-mail us
                            </Grid>
                            <Grid item>
                            info@demolink.org
                            </Grid>
                        </Grid>
                  </Grid>
                  <Grid container sm={4} spacing={4} direction="row"  alignItems="center"  item>
                    <Grid item>
                        <AccessTimeIcon />
                    </Grid>
                    <Grid item>
                        <Grid item>
                        Opening Time
                        </Grid>
                        <Grid item>
                        08.00 - 18.00
                        </Grid>
                    </Grid>
                  </Grid>
                </Grid>
              </Grid>
            </Container>
          </div>
          <Container className={classes.cardGrid} maxWidth="md">
            {/* End hero unit */}
            <Grid container spacing={4}>
              {cards.map(card => (
                <Grid item key={card} xs={12} sm={6} md={4}>
                  <Card className={classes.card}>
                    <CardMedia
                      className={classes.cardMedia}
                      image="https://source.unsplash.com/random"
                      title="Image title"
                    />
                    <CardContent className={classes.cardContent}>
                      <Typography gutterBottom variant="h5" component="h2">
                        Heading
                      </Typography>
                      <Typography>
                        This is a media card. You can use this section to describe the content.
                      </Typography>
                    </CardContent>
                    <CardActions>
                      <Button size="small" color="primary">
                        View
                      </Button>
                      <Button size="small" color="primary">
                        Edit
                      </Button>
                    </CardActions>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Container>
        </main>
        {/* Footer */}
        <footer className={classes.footer}>
          <Typography variant="h6" align="center" gutterBottom>
            Footer
          </Typography>
          <Typography variant="subtitle1" align="center" color="textSecondary" component="p">
            Something here to give the footer a purpose!
          </Typography>
          <Copyright />
        </footer>
        {/* End footer */}
      </React.Fragment>
      </div>

    );
}

export default Home;
